import {
  AppBar,
  Tabs,
  Tab,
  Grid,
  Toolbar,
  useMediaQuery,
  useTheme,
  Typography,
} from "@mui/material";
import React, { useState } from "react";
import { styled } from "@mui/material/styles";
import Paper from "@mui/material/Paper";
import { Link } from "react-router-dom";
import AS from "../logo.png";
import DrawerComp from "./DrawerComp";

// Array to display list of
const PAGES = ["Sample01", "Sample02", "SignUp", "SignIn", "SignOut"];

// styling for the hamburger with colors and paper
const Item = styled(Paper)(({ theme }) => ({
  backgroundColor: theme.palette.mode === "light" ? "#074588" : "light",
  ...theme.typography.body2,
  padding: theme.spacing(1),
  textAlign: "center",
  color: theme.palette.text.secondary,
}));

export const Header = () => {
  // "value" for navbar button selection
  const [value, setValue] = useState();

  // inbult theme for navbar
  const theme = useTheme();
  console.log(theme);

  // checking if the screen is below md and then placing hamburger menu
  const isMatch = useMediaQuery(theme.breakpoints.down("md"));
  console.log(isMatch);

  return (
    <React.Fragment>
      {/* app bar with blue color */}
      <AppBar sx={{ background: "#063970" }}>
        <Toolbar>
          {isMatch ? (
            <>
            // if statememt for md view
              {/*three grids for keeping logo in the middle */}
              <Grid
                container
                direction="row"
                justifyContent="space-between"
                alignItems="center"
              >
                <Grid md={4}>
                  <Item>
                    {/*hamburger menu */}
                    <DrawerComp />
                  </Item>
                </Grid>
                <Grid md={4}>
                  <Link to="/">
                    <img
                      src={"https://cdn.worldvectorlogo.com/logos/redbull.svg"}
                      alt="logo"
                      height={50}
                    />
                  </Link>
                </Grid>
                <Grid md={4}></Grid>
              </Grid>
            </>
          ) : (
            // else statememt for fullscreen view
            <>
              <Link to="/" sx={{ paddingLeft: "50%" }}>
                <img
                  src={"https://cdn.worldvectorlogo.com/logos/redbull.svg"}
                  alt="logo"
                  marginLeft="auto"
                  height={50}
                />
              </Link>
              <Tabs
                sx={{ marginLeft: "auto" }}
                value={value}
                textColor="inherit"
                indicatorColor="secondary"
                onChange={(e, value) => setValue(value)}
              >
                {PAGES.map((page, index) => (
                  <Tab key={index} label={page} />
                ))}
              </Tabs>
            </>
          )}
        </Toolbar>
      </AppBar>
    </React.Fragment>
  );
};
